-- Migration file for adding product_characteristics and washing_instructions tables\n\n-- product_characteristics table\nCREATE TABLE product_characteristics (\n    id SERIAL PRIMARY KEY,\n    product_id INT NOT NULL,\n    characteristic_name VARCHAR(255) NOT NULL,\n    characteristic_value VARCHAR(255),\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW() ON UPDATE NOW()\n);\n\n-- washing_instructions table\nCREATE TABLE washing_instructions (\n    id SERIAL PRIMARY KEY,\n    product_id INT NOT NULL,\n    instruction TEXT NOT NULL,\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW() ON UPDATE NOW()\n);\n\n-- Add foreign key constraints\nALTER TABLE product_characteristics ADD CONSTRAINT fk_product\n    FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE CASCADE;\n\nALTER TABLE washing_instructions ADD CONSTRAINT fk_product_instructions\n    FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE CASCADE;